import { getStoryblokApi } from "@/lib/storyblok/storyblokApi";
import { ISbLinksParams } from "storyblok-js-client";
import { z } from "zod";
import invariant from "tiny-invariant";

export const revalidate = 60;

const sbParams: ISbLinksParams = {
  version: "draft",
};

const paramsSchema = z.object({
  slug: z.array(z.string()),
});

type Params = z.infer<typeof paramsSchema>;

export async function getStaticPaths() {
  const storyblokApi = getStoryblokApi();
  const { data } = await storyblokApi.get(`cdn/links`, sbParams);

  console.log({ data });
  invariant(data.links, "links are missing");

  const links = data.links;
  const paths: { params: Params }[] = [];

  Object.keys(links).forEach((linkKey) => {
    if (links[linkKey].is_folder || links[linkKey].slug === "home") {
      return;
    }

    const slug = links[linkKey].slug;
    invariant(slug, "slug is missing");

    const splittedSlug = slug.split("/");

    paths.push({ params: { slug: splittedSlug } });
  });

  return {
    paths,
    fallback: false,
  };
}

export default async function Page({ params }: { params: Params }) {
  const { slug } = await z.promise(paramsSchema).parseAsync(params);

  const storyblokApi = getStoryblokApi();
  const { data } = await storyblokApi.get(`cdn/stories/${slug}`, sbParams);

  if (!data.story) {
    return <div>Story not found</div>;
  }

  return (
    <div>
      <h1>{slug}</h1>
    </div>
  );
}
